<script>
import { setTimeout } from 'timers'
/* eslint-disable */

export default {
  name: 'gamenotice',
  data () {
    return {
      active: false,
      page: '',
      togameHall: false,
      shenzhan: false,
      chipnotice: false,
    }
  },
  methods: {
    allSwitch: function () {
      if (this.page == 'DOUNIU' || this.page == 'GAME' || this.page == "QUEJIU" || this.page == "SANGONG" || this.page == "SHAIBAOGAME" || this.page == "LUNPANGAME") {
        this.active = false;
        var gameId = this.$store.state.ziXuanDesk[0];
        console.log(gameId)
        this.global.sendMessage("exit_desk", { desk_id: gameId });
        this.$store.commit('TOGAMEHALL', true)

      } else {
        this.active = !this.active;
        this.$store.dispatch('ShowAll', this.active)
      }
    },
    isshanzhan: function () {
      this.shenzhan = !this.shenzhan
    }
  },
  mounted () {
    if (this.$store.state.hallnotice == "0" || this.$store.state.hallnotice == "2") {
      this.chipnotice = false
    } else if (this.$store.state.hallnotice == "1") {
      this.chipnotice = true

    }
  },
  watch: {
    '$store.state.hallnotice': function () {
      if (this.$store.state.hallnotice == "0" || this.$store.state.hallnotice == "2") {
        this.chipnotice = false
      } else if (this.$store.state.hallnotice == "1") {
        this.chipnotice = true
      }
    },
    '$store.state.onPushOutMember': function () {
      var gameId = this.$store.state.ziXuanDesk[0];
      var self = this
      setTimeout(function () {
        self.$store.commit('ONEEXITDESK', '服务退桌子')
        self.$store.commit('TOGAMEHALL', true)
        console.log("退桌子到大厅")
      }, 2000)

    },
    '$store.state.ziXuan': function (vo) {
      if (this.$store.state.page == 'DOUNIU' || this.$store.state.page == 'GAME' || this.$store.state.page == 'QUEJIU' || this.$store.state.page == 'SHAIBAOGAME' || this.$store.state.page == 'LUNPANGAME' || this.$store.state.page == "SANGONG") {
        // 可以进大厅
        console.log("gnotice自选====", vo)
        this.$store.state.playerGame.dispose()
        if (this.$store.state.page == 'LUNPANGAME') {
          if (this.$store.state.LPplayerGame && !this.$store.state.LPplayerGame.disposed) {
            this.$store.state.LPplayerGame.dispose()
            this.$set(this.$store.state.LPplayerGame, 'disposed', true)
          }
        }

        this.$store.commit('PLAYERGAME', "")
        this.$router.push('/')
        this.$store.dispatch('SetuPage', 'GAMEHALL')
        this.$store.commit('GAMEREADY', false)
      }
    },
    '$store.state.oneExitDesk': function () {
      console.log("gnotice自选====", this.$store.state.togamehall)
      // 退桌 进大厅
      if (this.$store.state.togamehall) {
        this.global.sendMessage('zixuan_enter_desk', { bet_addition_id: this.$store.state.userLimitID })
      }
    },
    '$store.state.page': function () {
      this.page = this.$store.state.page
      if (this.page == 'GAME') {
        this.active = false
      }
      if (this.page == 'GAMEHALL') {
        this.active = false
      }
    }
  },
}
</script>

